var async = require("async");
var React = require("react");
var Router = require("react-router");
var routes = require("../app/" + __resourceQuery.substr(1) + "Routes");
var stores = require("../app/" + __resourceQuery.substr(1) + "Stores");
var withTimeout = require("./withTimeout");
var ReactUpdates = require("react/lib/ReactUpdates");

var initialRun = true;

// react-router handles location
Router.run(routes, Router.HistoryLocation, function(Application, state) {

	// On every page navigation invalidate data from the stores
	// This is not needed when the server notifies the client about changes (WebSocket, SSE)
	if(!initialRun) {
		Object.keys(stores).forEach(function(key) {
			stores[key].outdate();
		});
	}
	initialRun = false;

	ReactUpdates.batchedUpdates(function() {
		stores.Router.setItemData("transition", state);
	});

	// try to fetch data for a defined timespan
	// when the data is not fully fetched after the timeout components are rendered (with missing/old data)
	withTimeout(async.forEach.bind(async, state.routes, function(route, callback) {
		if(route.handler.chargeStores) {
			route.handler.chargeStores(stores, state.params, callback);
		} else {
			callback();
		}
	}), 600, function() {

		ReactUpdates.batchedUpdates(function() {
			stores.Router.setItemData("transition", null);
		});

		// Render the components with the stores
		React.withContext({
			stores: stores
		}, function() {
			React.render(React.createElement(Application, null), document.getElementById("content"));
		});
	});
});

var io = require("./web_modules/socket.io");
var scriptElements = document.getElementsByTagName("script");
io = io.connect(typeof __resourceQuery === "string" && __resourceQuery ?
	__resourceQuery.substr(1) :
	scriptElements[scriptElements.length-1].getAttribute("src").replace(/\/[^\/]+$/, "")
);

var hot = false;
var initial = true;
var currentHash = "";

io.on("hot", function() {
	hot = true;
	console.log("[WDS] Hot Module Replacement enabled.");
});

io.on("invalid", function() {
	console.log("[WDS] App updated. Recompiling...");
});

io.on("hash", function(hash) {
	currentHash = hash;
});

io.on("ok", function() {
	if(initial) return initial = false;
	reloadApp();
});

io.on("warnings", function(warnings) {
	console.log("[WDS] Warnings while compiling.");
	for(var i = 0; i < warnings.length; i++)
		console.warn(warnings[i]);
	if(initial) return initial = false;
	reloadApp();
});

io.on("errors", function(errors) {
	console.log("[WDS] Errors while compiling.");
	for(var i = 0; i < errors.length; i++)
		console.error(errors[i]);
	if(initial) return initial = false;
	reloadApp();
});

io.on("proxy-error", function(errors) {
	console.log("[WDS] Proxy error.");
	for(var i = 0; i < errors.length; i++)
		console.error(errors[i]);
	if(initial) return initial = false;
	reloadApp();
});

io.on("disconnect", function() {
	console.error("[WDS] Disconnected!");
});

function reloadApp() {
	if(hot) {
		console.log("[WDS] App hot update...");
		window.postMessage("webpackHotUpdate" + currentHash, "*");
	} else {
		console.log("[WDS] App updated. Reloading...");
		window.location.reload();
	}
}

var React = require("react");
var Router = require("react-router");
var Route = Router.Route;
var DefaultRoute = Router.DefaultRoute;

// polyfill
if(!Object.assign)
	Object.assign = React.__spread;

// export routes
module.exports = (
	React.createElement(Route, {name: "app", path: "/", handler: require("./Application")}, 
		React.createElement(Route, {name: "some-page", path: "/some-page", handler: require("./SomePage")}), 
		React.createElement(Route, {name: "todolist", path: "/:list", handler: require("./TodoList")}), 
		React.createElement(Route, {name: "todoitem", path: "/todo/:item", handler: require("./TodoItem")}), 
		React.createElement(DefaultRoute, {name: "home", handler: require("./Home")})
	)
);

var ItemsStore = require("items-store/ItemsStore");
var async = require("async");
var request = require("superagent");
var ReactUpdates = require("react/lib/ReactUpdates");

// a few helper methods for a REST API

function batchedCallback(callback) {
	return function(err, res) {
		ReactUpdates.batchedUpdates(callback.bind(null, err, res));
	}
}

function writeAndReadSingleItem(path, resultHandler) {
	resultHandler = resultHandler || function(result) { return result; };
	return function(options, callback) {
		request.post(path + options.id)
			.set("Accept", "application/json")
			.type("json")
			.send(options.update)
			.end(batchedCallback(function(err, res) {
				if(err) return callback(err);
				callback(null, resultHandler(res.body));
			}));
	}
}

function readSingleItem(path, resultHandler) {
	resultHandler = resultHandler || function(result) { return result; };
	return function(options, callback) {
		request.get(path + options.id)
			.set("Accept", "application/json")
			.type("json")
			.end(batchedCallback(function(err, res) {
				if(err) return callback(err);
				callback(null, resultHandler(res.body));
			}));
	}
}

function readMultipleItems(path, resultHandler) {
	resultHandler = resultHandler || function(result) { return result; };
	return function(optionsArr, callback) {
		request.get(path + optionsArr.map(function(options) {
			return options.id;
		}).join("+"))
			.set("Accept", "application/json")
			.type("json")
			.end(batchedCallback(function(err, res) {
				if(err) return callback(err);
				callback(null, resultHandler(res.body));
			}));
	}
}

// a queue that allows only one REST request at a time
// it also defers the requests to next tick, to aggregate multiple changes
var queue = async.queue(function(fn, callback) {
	process.nextTick(function() {
		fn(callback);
	});
}, 1);

// load embedded initial store data from prerendering if available
var initialData = typeof __StoreData === "object" ? __StoreData : {};

// take the store descriptions as base
var desc = require("./mainStoresDescriptions");

var stores = module.exports = {
	Router: new ItemsStore(desc.Router),

	TodoList: new ItemsStore(Object.assign({
		// REST API at "/_/list/"
		// the API also returns "TodoItem"s for requests

		writeAndReadSingleItem: writeAndReadSingleItem("/_/list/", function(result) {
			Object.keys(result.items).forEach(function(key) {
				stores.TodoItem.setItemData(key.substr(1), result.items[key]);
			});
			return result.list;
		}),
		readSingleItem: readSingleItem("/_/list/", function(result) {
			Object.keys(result.items).forEach(function(key) {
				stores.TodoItem.setItemData(key.substr(1), result.items[key]);
			});
			return result.list;
		}),

		queueRequest: queue.push.bind(queue),
	}, desc.TodoList), initialData.TodoList),

	TodoItem: new ItemsStore(Object.assign({
		// REST API at "/_/todo"
		// it supports reading up to 10 items at once

		writeAndReadSingleItem: writeAndReadSingleItem("/_/todo/"),
		readSingleItem: readSingleItem("/_/todo/"),
		readMultipleItems: readMultipleItems("/_/todo/"),

		queueRequest: queue.push.bind(queue),
		maxWriteItems: 10
	}, desc.TodoItem), initialData.TodoItem)
};


// bind actions to stores

var actions = require("./actions");

actions.Todo.add.listen(function(list, item) {
	stores.TodoList.updateItem(list, { $push: [item] });
});

actions.Todo.update.listen(function(id, update) {
	stores.TodoItem.updateItem(id, update);
});

module.exports = function(fn, timeout, callback) {
	var timedOut = false;
	var to = setTimeout(function() {
		timedOut = true;
		callback();
	}, timeout);
	fn(function() {
		clearTimeout(to);
		if(!timedOut) callback();
	});
};

// shim for using process in browser

var process = module.exports = {};

process.nextTick = (function () {
    var canSetImmediate = typeof window !== 'undefined'
    && window.setImmediate;
    var canMutationObserver = typeof window !== 'undefined'
    && window.MutationObserver;
    var canPost = typeof window !== 'undefined'
    && window.postMessage && window.addEventListener
    ;

    if (canSetImmediate) {
        return function (f) { return window.setImmediate(f) };
    }

    var queue = [];

    if (canMutationObserver) {
        var hiddenDiv = document.createElement("div");
        var observer = new MutationObserver(function () {
            var queueList = queue.slice();
            queue.length = 0;
            queueList.forEach(function (fn) {
                fn();
            });
        });

        observer.observe(hiddenDiv, { attributes: true });

        return function nextTick(fn) {
            if (!queue.length) {
                hiddenDiv.setAttribute('yes', 'no');
            }
            queue.push(fn);
        };
    }

    if (canPost) {
        window.addEventListener('message', function (ev) {
            var source = ev.source;
            if ((source === window || source === null) && ev.data === 'process-tick') {
                ev.stopPropagation();
                if (queue.length > 0) {
                    var fn = queue.shift();
                    fn();
                }
            }
        }, true);

        return function nextTick(fn) {
            queue.push(fn);
            window.postMessage('process-tick', '*');
        };
    }

    return function nextTick(fn) {
        setTimeout(fn, 0);
    };
})();

process.title = 'browser';
process.browser = true;
process.env = {};
process.argv = [];

function noop() {}

process.on = noop;
process.addListener = noop;
process.once = noop;
process.off = noop;
process.removeListener = noop;
process.removeAllListeners = noop;
process.emit = noop;

process.binding = function (name) {
    throw new Error('process.binding is not supported');
};

// TODO(shtylman)
process.cwd = function () { return '/' };
process.chdir = function (dir) {
    throw new Error('process.chdir is not supported');
};

/*!
